import React, { useState } from 'react';

const Song = ({ id, title, artist, duration }) => {
    const [liked, setLiked] = useState(false);

    const handleLike = () => {
        setLiked(!liked);
    };

    return (
        <li className="flex items-center py-3 px-4 hover:bg-hoverGray transition duration-300 ease-in-out">
            <div className="flex-grow">
                <h3 className="text-lg font-semibold text-teal">{title}</h3>
                <p className="text-sm text-gray-500">{artist}</p>
            </div>
            <p className="text-sm text-gray-500">{duration}</p>
            <button onClick={handleLike} className={`ml-2 focus:outline-none border-2 rounded-full p-1 ${liked ? 'bg-teal text-white' : 'text-teal border-teal'}`}>
                <span role="img" aria-label="Like">&#x2665;</span>
            </button>
        </li>
    );
};

export default Song;
