import React, { useState , useEffect} from 'react';
import Navbar from './components/Navbar/Navbar';
import Playlist from './components/Playlist/Playlist';
import Footer from './components/Footer/Footer';
import About from './pages/About/About';
import Login from './pages/Login/Login'
import './App.css';

import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Assuming you have a function to check if the user is logged in based on the access token
  const checkLoginStatus = () => {
    const accessToken = localStorage.getItem('token');
    if (accessToken) {
      setIsLoggedIn(true);
      console.log("is ogged in");
    } else {
      setIsLoggedIn(false);
    }
  };

  // Call the function to check login status on initial render
  useEffect(() => {
    checkLoginStatus();
    const intervalId = setInterval(() => {
      checkLoginStatus();
    }, 5000); 
    return () => clearInterval(intervalId);
  },[]);

  return (
    <Router>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/SpotifyAPITest" element={isLoggedIn ?<Playlist /> : <Login />} />
          <Route path="/about" element={<About />} />
          {/* Redirect to login page if the user is not logged in */}
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}


export default App;


