import React, { useState, useEffect } from 'react';
import LikedIcon from '../Like/LikedIcon.jsx';
import UnlikedIcon from '../Like/UnlikedIcon.jsx';

const Like = ({ songId }) => {
  const [liked, setLiked] = useState(() => {
    const playlist = JSON.parse(localStorage.getItem('playlist')) || [];
    const song = playlist.find(song => song.id === songId);
    return song ? song.liked || false : false;
  });

  useEffect(() => {
    const playlist = JSON.parse(localStorage.getItem('playlist')) || [];
    const updatedPlaylist = playlist.map(song => {
      if (song.id === songId) {
        return { ...song, liked };
      }
      return song;
    });
    localStorage.setItem('playlist', JSON.stringify(updatedPlaylist));
  }, [liked, songId]);

  const toggleLiked = () => {
    setLiked(!liked);
  };

  return (
    <div onClick={toggleLiked}>
      {liked ? <LikedIcon /> : <UnlikedIcon />}
    </div>
  );
};

export default Like;
