import React, { useState } from 'react';

const AddSong = () => {
  const [songName, setSongName] = useState('');
  const [artist, setArtist] = useState('');
  const [addedSong, setAddedSong] = useState(null); // State to store the added song details

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:8001/addsong', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ songname: songName, songartist: artist }), // Use correct field names
      });
      if (!response.ok) {
        throw new Error('Failed to add song');
      }
      const data = await response.json(); // Parse JSON response
      setAddedSong(data); // Update state with added song details
      console.log('Song added successfully:', data);

      // Update local storage playlist
    //   let playlist = JSON.parse(localStorage.getItem('playlist')) || [];
    //   playlist.push({
    //     id: playlist.length + 1, // Generate a new id for the song
    //     title: data.songname,
    //     artist: data.songartist,
    //     duration: data.duration,
    //     liked: false, // Default value for liked status
    //   });
    //   localStorage.setItem('playlist', JSON.stringify(playlist));
    } catch (error) {
      console.error('Error adding song:', error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold">Add Song</h1>
      <form onSubmit={handleSubmit} className="mt-4">
        <div className="mb-4">
          <label htmlFor="songName" className="block text-gray-700 font-bold mb-2">Song Name</label>
          <input
            type="text"
            id="songName"
            value={songName}
            onChange={(e) => setSongName(e.target.value)}
            className="border rounded-md px-3 py-2 w-full"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="artist" className="block text-gray-700 font-bold mb-2">Artist</label>
          <input
            type="text"
            id="artist"
            value={artist}
            onChange={(e) => setArtist(e.target.value)}
            className="border rounded-md px-3 py-2 w-full"
            required
          />
        </div>
        <button type="submit" className="bg-teal hover:bg-hoverGray text-white font-bold py-2 px-4 rounded">Add Song</button>
      </form>

      {/* Display added song details */}
      {addedSong && (
        <div className="mt-4">
          <h2 className="text-lg font-semibold">Added Song Details:</h2>
          <p>Song Name: {addedSong.songname}</p>
          <p>Artist: {addedSong.songartist}</p>
          <p>Duration: {addedSong.duration}</p>
        </div>
      )}
    </div>
  );
};

export default AddSong;
