import UnlikedIcon from '../Like/UnlikedIcon.jsx';

const Like = ({ songId , onHandleLike}) => {
  const [liked, setLiked] = useState(false);
  const playlist = JSON.parse(localStorage.getItem('playlist')) || [];

  const HandleLike =() => {
    const song = playlist.find(song => song.id === songId);
    console.log(song);

    if (song && song.liked) {
      setLiked(true);
    }else{
      song.liked = !song.Liked
      const index = playlist.findIndex(song => song.id === songId);

      // If the song with ID 4 exists in the playlist
      if (index !== -1) {
        // Replace the song with ID 4 with the songMock
        playlist[index] = songMock;
      }      localStorage.setItem('playlist', JSON.stringify(playlist));

    }

  }

  const toggleLiked = () => {
    setLiked(prevLiked => !prevLiked);
    HandleLike(playlist)
  };

  return (
    <div onClick={toggleLiked}>
      {liked ? <LikedIcon /> : <UnlikedIcon />}
    </div>
  );
};

export default Like;
