import React from 'react';

const Navbar = ({ activePage, setActivePage, darkMode, setDarkMode }) => {
  const handleToggleDarkMode = () => {
    setDarkMode((prevDarkMode) => !prevDarkMode);
  };

  const handleAboutClick = () => {
    setActivePage('about');
  };

  const handlePlaylistClick = () => {
    setActivePage('playlist');
  };

  return (
    <nav className={`bg-${darkMode ? 'darkGray' : 'lightGray'} p-4`}>
      <div className="container mx-auto flex justify-between items-center">
        <a href="/" className="text-teal text-3xl font-bold flex items-center">
          <span className="mr-2">Playlistify</span>
        </a>
        <div className="flex items-center">
          <button
            className="mr-4 bg-teal hover:bg-hoverGray text-white font-bold py-2 px-4 rounded"
            onClick={handleToggleDarkMode}
          >
            {darkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
          <button
            className={`mr-4 ${
              activePage === 'playlist' ? 'text-teal font-bold' : 'text-gray-500'
            }`}
            onClick={handlePlaylistClick}
          >
            Playlist
          </button>
          <button
            className={`${
              activePage === 'about' ? 'text-teal font-bold' : 'text-gray-500'
            }`}
            onClick={handleAboutClick}
          >
            About
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
