import React from 'react';

const Navbar = ({ currentPlaylist, darkMode, onAddMusic, onToggleDarkMode }) => {
  return (
    <nav className={`bg-${darkMode ? 'darkGray' : 'lightGray'} p-4`}>
      <div className="container mx-auto flex justify-between items-center">
        <a href="/" className="text-teal text-xl font-bold">Spotify Top Songs</a>
        <div className="flex items-center">
          <span className="text-white mr-4">Current Playlist: {currentPlaylist}</span>
          <button 
            className={`bg-teal hover:bg-hoverGray text-white font-bold py-2 px-4 rounded ${darkMode ? 'dark' : 'light'}-mode-btn`}
            onClick={onAddMusic}
          >
            Add Music
          </button>
          <button 
            className="bg-teal hover:bg-hoverGray text-white font-bold py-2 px-4 rounded ml-4"
            onClick={onToggleDarkMode}
          >
            {darkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
