function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <Router>
      <div className="App">
        <Navbar />
        <Switch>
          <Route path="/login">
            <Login onLogin={() => setIsLoggedIn(true)} />
          </Route>
          <Route path="/playlist">
            {isLoggedIn ? <Playlist /> : <Redirect to="/login" />}
          </Route>
          <Route path="/about">
            {isLoggedIn ? <About /> : <Redirect to="/login" />}
          </Route>
          <Redirect from="/" to="/login" />
        </Switch>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
