import React, { useState, useEffect } from 'react';
import Song from '../Song/Song';
import Shuffle from '../Shuffle/Shuffle';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

const Playlist = () => {
    const [playlist, setPlaylist] = useState([]);

    const handleShuffle = (shuffledPlaylist) => {
        setPlaylist(shuffledPlaylist);
    };

    const handleLike = (updatedPlaylist) => {
        setPlaylist(updatedPlaylist);
        localStorage.setItem('playlist', JSON.stringify(updatedPlaylist));
    };

    useEffect(() => {
        const fetchPlaylist = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8001/songs');
                if (!response.ok) {
                    throw new Error('Failed to fetch playlist');
                }
                const songsData = await response.json();
                setPlaylist(songsData);
                localStorage.setItem('playlist', JSON.stringify(songsData));
                console.log("fetched");
            } catch (error) {
                console.error('Error fetching playlist:', error);
            }
        };

        fetchPlaylist();
    }, []);
    return (
        <div className="playlist bg-lightTeal text-black p-4 rounded-lg shadow-lg w-3/4 mx-auto">
        <h2 className="text-xl font-semibold text-teal mb-4">
            My Daily Playlist
        </h2>
        <div className="flex justify-between items-center mb-4">
            <Shuffle onShuffle={handleShuffle} className="bg-teal hover:bg-hoverGray text-white font-bold py-2 px-4 rounded inline-block mr-2" />
            <div className="flex-center">
                <Link to="/addsong" className="bg-teal hover:bg-hoverGray text-white font-bold py-2 px-4 mr-2 rounded inline-block">Add Song</Link>
                <Link to="/deletesong" className="bg-teal hover:bg-hoverGray text-white font-bold py-2 px-4 rounded inline-block">Delete Song</Link>
            </div>
        </div>
        <ul>
            {playlist.map((song, index) => (
                <Song
                    key={song.id}
                    title={song.title}
                    artist={song.artist}
                    duration={song.duration}
                    className="mb-4"
                    onHandleLike={handleLike}
                />
            ))}
        </ul>
    </div>
);
};

export default Playlist;
