import React from 'react';
import { Link } from 'react-router-dom'; // Import Link from React Router

const About = () => {
  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold">What is this?</h1>
      <p>Welcome, user.</p>
      <p>Playlistify is a music application that allows you to shuffle your daily playlist effectively.</p>
      {/* Use Link to navigate to the /about endpoint */}
      <Link to="/about" className="text-blue-500 hover:underline">Learn more</Link>
    </div>
  );
};

export default About;
