import React, { useState, useEffect } from 'react';
import LikedIcon from '../Like/LikedIcon.jsx';
import UnlikedIcon from '../Like/UnlikedIcon.jsx';

const Like = ({ songId }) => {
  const [liked, setLiked] = useState(false);

  useEffect(() => {
    const playlist = JSON.parse(localStorage.getItem('playlist')) || [];
    const song = playlist.find(song => song.id === songId);
    if (song && song.liked) {
      setLiked(true);
    }
  }, [songId]);

  const toggleLiked = () => {
    // Get the playlist from local storage
    const playlist = JSON.parse(localStorage.getItem('playlist')) || [];

    // Find the song with the specified ID
    const songIndex = playlist.findIndex(song => song.id === songId);

    // If the song is found, toggle its liked property
    if (songIndex !== -1) {
      playlist[songIndex].liked = !playlist[songIndex].liked;

      // Update the playlist in local storage
      localStorage.setItem('playlist', JSON.stringify(playlist));

      // Update the liked state
      setLiked(prevLiked => !prevLiked);
    }
  };

  return (
    <div onClick={toggleLiked}>
      {liked ? <LikedIcon /> : <UnlikedIcon />}
    </div>
  );
};

export default Like;
