import React, { useState, useEffect } from 'react';
import LikedIcon from '../Like/LikedIcon.jsx';
import UnlikedIcon from '../Like/UnlikedIcon.jsx';

const Like = ({ songId }) => {
  const [liked, setLiked] = useState(false);

  useEffect(() => {
    const likedSongs = JSON.parse(localStorage.getItem('likedSongs')) || [];
    setLiked(likedSongs.includes(songId));
  }, [songId]);

  const toggleLiked = () => {
    const likedSongs = JSON.parse(localStorage.getItem('likedSongs')) || [];
    if (likedSongs.includes(songId)) {
      const updatedLikedSongs = likedSongs.filter(id => id !== songId);
      localStorage.setItem('likedSongs', JSON.stringify(updatedLikedSongs));
      setLiked(false);
    } else {
      likedSongs.push(songId);
      localStorage.setItem('likedSongs', JSON.stringify(likedSongs));
      setLiked(true);
    }
  };

  return (
    <div onClick={toggleLiked}>
      {liked ? <LikedIcon /> : <UnlikedIcon />}
    </div>
  );
};

export default Like;
