import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ darkMode, setDarkMode }) => {
  const handleToggleDarkMode = () => {
    setDarkMode((prevDarkMode) => !prevDarkMode);
  };

  return (
    <nav className={`bg-${darkMode ? 'darkGray' : 'lightGray'} p-4`}>
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-teal text-3xl font-bold flex items-center">
          <span className="mr-2">Playlistify</span>
        </Link>
        <div className="flex items-center">
          <button
            className="mr-4 bg-teal hover:bg-hoverGray text-white font-bold py-2 px-4 rounded"
            onClick={handleToggleDarkMode}
          >
            {darkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
          <Link to="/playlist" className="mr-4 text-teal font-bold">Playlist</Link>
          <Link to="/about" className="text-teal font-bold">About</Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
