import React, { useState, useEffect } from 'react';
import LikedIcon from '../Like/LikedIcon.jsx';
import UnlikedIcon from '../Like/UnlikedIcon.jsx';

const Like = ({ songId }) => {
  const [liked, setLiked] = useState(false);

  useEffect(() => {
    const playlist = JSON.parse(localStorage.getItem('playlist')) || {};
    const song = playlist[songId]; // Access song object using songId as key
    if (song && song.liked) {
      setLiked(true);
    }
  }, [songId]);

  const toggleLiked = async () => {
    // Update liked state in the component (immediate update)
    setLiked(prevLiked => !prevLiked);

    try {
      // Update liked status in local storage
      const playlist = JSON.parse(localStorage.getItem('playlist')) || {};
      console.log(playlist);
      console.log(songId)

      const songIndex = playlist.findIndex(song => song.id === songId);
        console.log(songIndex);
        if (songIndex !== -1) {
            // Create a copy of the song object
            const updatedSong = { ...playlist[songIndex] };

            // Update the liked property of the copied song object
            updatedSong.liked = true;

            // Replace the original song object with the updated one in the playlist array
            playlist[songIndex] = updatedSong;


      //const song = playlist[songId]; // Access song object using songId as key
     // console.log(song);


      if (updatedSong) {
        //song.liked = true; // Set liked property to true for the song object
        await localStorage.setItem('playlist', JSON.stringify(playlist));
      }
    }
    } catch (error) {
      console.error('Error updating playlist in local storage:', error);
      // Handle potential errors during localStorage update (optional)
    }
  };

  return (
    <div onClick={toggleLiked}>
      {liked ? <LikedIcon /> : <UnlikedIcon />}
    </div>
  );
};

export default Like;
